mapper.update:
               
		update request_tbl
			set REQUEST_STATUS = '접수중'
			where REQUEST_NUM = #{requestNum}

               
		update request_tbl
			set REQUEST_DELETE = 'Y'
			where REQUEST_NUM = #{requestNum}

               
		update request_tbl
			set REQUEST_DELETE = 'N'
			where REQUEST_NUM = #{requestNum}
