mapper.select:


SELECT COMMENT_CONTENT 
	FROM COMMENT_TBL
	WHERE COMMENT_ORIGINALNUM = #{goodsQNANum}

mapper.update:

     	update COMMENT_TBL
		set COMMENT_DELETE = 'Y'
		where COMMENT_NUM = #{commentNum}


     	update COMMENT_TBL
		set COMMENT_CONTENT = #{commentContent}
		where COMMENT_NUM = #{commentNum}
