mapper.select:
SELECT * 
FROM MEMBER_TBL
WHERE MEMBER_JOIN_API=#{memberJoinApi} 
AND MEMBER_JOIN_DI=#{memberJoinDi}

select * from member_tbl 
where member_email=#{memberEmail} 
and member_password=#{memberPassword}
and member_grade != '0'

select * from member_tbl
where member_mobile=#{memberMobile}
and member_name=#{memberName}
and member_grade != '0'


	and member_email = #{memberEmail}

mapper.update:
update member_tbl 
set member_password=#{memberPassword} 
where member_email=#{memberEmail}

update member_tbl set member_name=#{memberName}
, member_zipcode=#{memberZipcode},
, member_detail_address=#{memberDetailAddress},
, member_zipcode=#{memberAddress},
,member_address=#{memberAddress}, member_detail_address=#{memberDetailAddress}, member_modify_date=sysdate where member_num=#{memberNum}

update member_tbl
set member_mobile=#{memberMobile}
where member_num=#{memberNum}

update member_tbl 
set member_class='S'
where member_num=#{memberNum}

update seller_tbl
set seller_bank_name=#{sellerBankName}, seller_account_num=#{sellerAccountNum}, seller_account_name=#{sellerAccountName}, seller_specialty=#{sellerSpecialty}, seller_description=#{sellerDescription}, seller_nickname=#{sellerNickName}, seller_contact1=#{sellerContact1}, seller_contact2=#{sellerContact2}, seller_buisness_yn=#{sellerBuisnessYn}
where seller_num=#{sellerNum}

update seller_tbl

set
seller_image=#{sellerImage}

where seller_num=#{sellerNum}
