mapper.select:


		SELECT QNA_NID, QNA_ORDERED, QNA_LAYER
          FROM QNA_TBL
          WHERE QNA_NUM = #{qnaNum}

mapper.update:

		   UPDATE QNA_TBL
             SET QNA_DELETE = 'Y'
             WHERE QNA_NUM = #{qnaNum}


		   UPDATE QNA_TBL
             SET QNA_TITLE = #{qnaTitle},
                 QNA_CONTENT = #{qnaContent}
             WHERE QNA_NUM = #{qnaNum}
