mapper.update:
update GOODS_TBL
set
	GOODS_STATUS = #{goodsStatus}
where
	GOODS_NUM = #{goodsNum}

mapper.select:
select count(distinct GOODS_OPT_NAME)
from GOODS_OPTION_TBL
where GOODS_NUM = #{goodsNum}

select *
from GOODS_OPTION_TBL
where GOODS_NUM = #{goodsNum}
and GOODS_OPT_NAME_NUM = #{goodsOptNameNum} 
order by GOODS_OPT_CONTENT_NUM ASC
