mapper.update:
UPDATE ORDER_TBL
SET ORDER_STATUS = #{ORDER_STATUS}
WHERE ORDER_NUM = #{ORDER_NUM};

UPDATE ORDER_OPT_TBL
SET ORDER_OPT_STATUS = #{ORDER_OPT_STATUS}
WHERE ORDER_NUM = #{ORDER_NUM};

UPDATE ORDER_OPT_TBL
SET ORDER_OPT_STATUS = #{ORDER_OPT_STATUS}
WHERE ORDER_NUM = #{ORDER_NUM};

UPDATE ORDER_TBL
SET ORDER_STATUS = #{ORDER_STATUS},
ORDER_CANCLE_DATE = TO CHAR(SYSDATE, 'YYYY-MM-DD');
WHERE ORDER_NUM = #{ORDER_NUM};

UPDATE ORDER_OPT_TBL
SET ORDER_OPT_STATUS = #{ORDER_OPT_STATUS},
ORDER_OPT_CANCLE_DATE = TO CHAR(SYSDATE, 'YYYY-MM-DD');
WHERE ORDER_NUM = #{ORDER_NUM};

UPDATE ORDER_TBL
SET ORDER_EMAIL = #{ORDER_EMAIL},
ORDER_CONTACT = #{ORDER_CONTACT},
ORDER_REQUEST = #{ORDER_REQUEST}
WHERE ORDER_NUM = #{ORDER_NUM};

UPDATE ORDER_OPT_TBL
SET ORDER_OPT_PRICE = #{ORDER_OPT_PRICE},
ORDER_OPT_COUNT = #{ORDER_OPT_COUNT},
ORDER_OPT_REQUEST = #{ORDER_OPT_REQUEST}
WHERE ORDER_NUM = #{ORDER_NUM};

mapper.select:
SELECT order_key_num, o.order_num, o.goods_num, member_num, order_status, order_contact, order_email, order_date, order_request, order_pay_type, order_name, order_format, order_resolution, order_size, order_modify_count, order_period, order_image, order_opt_num, o.goods_num, goods_opt_content_num, order_opt_price, order_opt_count, order_opt_date, order_opt_status, order_opt_request, order_opt_pay_type, order_opt_name, o.SELLER_NICKNAME
FROM order_tbl o, order_opt_tbl p
where o.order_num = p.order_num and o.ORDER_NUM = #{orderNum}
