mapper.select:

	SELECT REVIEW_NUM, REVIEW_WRITER, REVIEW_CONTENT, REVIEW_REGDATE, REVIEW_TITLE
	FROM REVIEW_TBL
	WHERE REVIEW_NUM =#{reviewNum}

mapper.update:
 	
 		update REVIEW_TBL
		set REVIEW_DELETE = 'Y'
		where REVIEW_NUM = #{reviewNum}

mapper.select:
select count(*) from REVIEW_TBL 
where 
REVIEW_NUM>0 AND REVIEW_DELETE ='N' AND GOODS_NUM=#{goodsNum}
